class ResponseBean {
  bool hasMore;
  String lastKey;

  String image;
  int type;

  // 赞数量
  int praiseCount;

  List<FeedsBean> feeds;
  List<BannersBean> banners;
  List<ColumnBean> columns;

  // 栏目板块
  ColumnBean column;

  ShareBean share;

  List<Option> options;

  PostBean post;
  List<Question> questions;

  Question question;

  // 42% 第一个问题
  Question genderQuestion;

  // 42% 区间问题
  Question slideQuestion;

  // 投票结果各个选项占比
  List<Option> everyoneAttitude;
  bool isRecord;

  // 评论
  List<QDailyComment> comments;
  int commentCount;

  AuthorBean author;

  // LABS 中的topic
  List<Topic> topics;

  static ResponseBean fromMap(Map<String, dynamic> map) {
    if (map == null) return null;
    ResponseBean responseBean = ResponseBean();
    responseBean.hasMore = map['has_more'];
    responseBean.commentCount = map['comment_count'];
    responseBean.praiseCount = map['praise_count'];
    responseBean.lastKey = '${map['last_key']}';
    responseBean.feeds = List()
      ..addAll((map['feeds'] as List ?? []).map((o) => FeedsBean.fromMap(o)));
    responseBean.banners = List()
      ..addAll(
          (map['banners'] as List ?? []).map((o) => BannersBean.fromMap(o)));
    responseBean.columns = List()
      ..addAll(
          (map['columns'] as List ?? []).map((o) => ColumnBean.fromMap(o)));
    responseBean.column =
        map['column'] == null ? null : ColumnBean.fromMap(map['column']);
    responseBean.share =
        map['share'] == null ? null : ShareBean.fromMap(map['share']);

    responseBean.post =
        map['post'] == null ? null : PostBean.fromMap(map['post']);
    responseBean.author =
        map['author'] == null ? null : AuthorBean.fromMap(map['author']);

    responseBean.options = List()
      ..addAll((map['options'] as List ?? []).map((o) => Option.fromMap(o)));

    responseBean.question =
        map['question'] == null ? null : Question.fromMap(map['question']);

    responseBean.questions = List()
      ..addAll(
          (map['questions'] as List ?? []).map((o) => Question.fromMap(o)));
    responseBean.genderQuestion = map['gender_question'] == null
        ? null
        : Question.fromMap(map['gender_question']);
    responseBean.slideQuestion = map['slide_question'] == null
        ? null
        : Question.fromMap(map['slide_question']);

    responseBean.comments = List()
      ..addAll(
          (map['comments'] as List ?? []).map((o) => QDailyComment.fromMap(o)));

    responseBean.topics = List()
      ..addAll(
          (map['paper_topics'] as List ?? []).map((o) => Topic.fromMap(o)));

    responseBean.isRecord = map['is_record'];
    responseBean.everyoneAttitude = List()
      ..addAll((map['everyones_attitude'] as List ?? [])
          .map((o) => Option.fromMap(o)));

    responseBean.image = map['image'];
    responseBean.type = map['type'];
    return responseBean;
  }

  @override
  String toString() {
    return '{hasMore: $hasMore, lastKey: $lastKey, image: $image, type: $type, feeds: $feeds, banners: $banners, columns: $columns, column: $column, share: $share, options: $options, post: $post, questions: $questions, genderQuestion: $genderQuestion, slideQuestion: $slideQuestion, everyoneAttitude: $everyoneAttitude, isRecord: $isRecord, comments: $comments, commentCount: $commentCount, author: $author, topics: $topics}';
  }
}

