class PostBean {
  String title;
  String description;
  String publishTime;
  String startTime;
  String superTag;
  dynamic duration;
  int id;
  int genre;
  int commentCount;
  int praiseCount;
  int recordCount;
  AuthorBean author;
  CategoryBean category;
  String categoryTile;
  String image;

  bool commentStatus;

  /// 文章详情页页面样式：0 普通；1 全屏；
  int pageStyle;

  int postId;
  String appView;
  String filmLength;
  String dataType;
  ColumnBean column;

  String body;
  List<String> js;
  List<String> css;
  List<String> infoImage;

  PostBean(
      {this.title,
      this.description,
      this.publishTime,
      this.startTime,
      this.superTag,
      this.duration,
      this.id,
      this.genre,
      this.commentCount,
      this.praiseCount,
      this.recordCount,
      this.categoryTile,
      this.image,
      this.commentStatus,
      this.pageStyle,
      this.postId,
      this.appView,
      this.filmLength,
      this.dataType});

  static PostBean fromMap(Map<String, dynamic> map) {
    PostBean postBean = PostBean();
    postBean.title = map['title'];
    postBean.image = map['image'];
    postBean.description = map['description'];
    postBean.publishTime = (map['publish_time'] is int)
        ? formatDateByMs(map['publish_time'] * 1000,
            formats: [mm, '月', dd, '日'])
        : formatDateByStr(map['publish_time'], formats: [mm, '月', dd, '日']);
    postBean.startTime = "${map['start_time']}";
    postBean.superTag = map['super_tag'];
    postBean.duration = map['duration'];
    postBean.id = map['id'];
    postBean.genre = map['genre'];
    postBean.commentCount = map['comment_count'];
    postBean.recordCount = map['record_count'];
    postBean.praiseCount = map['praise_count'];
    postBean.author =
        map['author'] == null ? null : AuthorBean.fromMap(map['author']);
    postBean.category =
        map['category'] == null ? null : CategoryBean.fromMap(map['category']);

    postBean.commentStatus = map['comment_status'];
    postBean.pageStyle = map['page_style'];
    postBean.postId = map['post_id'];
    postBean.appView = map['appview'];
    postBean.filmLength = map['film_length'];
    postBean.dataType = map['datatype'];
    postBean.column =
        map['column'] == null ? null : ColumnBean.fromMap(map['column']);

    postBean.body = map['body'];
    postBean.js = List()
      ..addAll((map['js'] as List ?? []).map((o) => o.toString()));
    postBean.css = List()
      ..addAll((map['css'] as List ?? []).map((o) => o.toString()));
    postBean.infoImage = List()
      ..addAll((map['info_image'] as List ?? []).map((o) => o.toString()));

    return postBean;
  }
}

